package adapters

import (
	"encoding/hex"
	"erupe-ce/common/byteframe"
	"erupe-ce/config"
	"fmt"
	"io"
	"os"
	"path/filepath"

	"github.com/jmoiron/sqlx"
)

type QuestLoaderEventsFolder struct {
	erupeConfig *config.Config
	db          *sqlx.DB
}

func NewQuestLoaderEventsFolder(erupeConfig *config.Config, db *sqlx.DB) QuestLoader {
	var questLoader = &QuestLoaderEventsFolder{
		erupeConfig: erupeConfig,
		db:          db,
	}

	return questLoader
}

func (ql *QuestLoaderEventsFolder) QuestBinById(id string) (quest []byte, err error) {
	filepath := filepath.Join(ql.erupeConfig.BinPath, fmt.Sprintf("quests/%s.bin", id))
	quest, err = os.ReadFile(filepath)
	return
}

func (ql *QuestLoaderEventsFolder) Quests(_take uint16, skip uint16, _dev bool) (quests []byte, err error) {
	var totalCount, returnedCount uint16
	bf := byteframe.NewByteFrame()
	bf.WriteUint16(0)
	err = filepath.Walk(fmt.Sprintf("%s/events/", ql.erupeConfig.BinPath), func(path string, info os.FileInfo, callbackError error) error {
		if callbackError != nil {
			return callbackError
		} else if info.IsDir() {
			return nil
		}
		data, osError := os.ReadFile(path)
		if osError != nil {
			return osError
		} else {
			if len(data) > 850 || len(data) < 400 {
				return nil // Could be more or less strict with size limits
			} else {
				totalCount++
				if totalCount > skip && len(bf.Data()) < 60000 {
					returnedCount++
					bf.WriteBytes(data)
					return nil
				}
			}
		}
		return nil
	})

	if err != nil || totalCount == 0 {
		return
	}

	vsQuestItems := []uint16{1580, 1581, 1582, 1583, 1584, 1585, 1587, 1588, 1589, 1595, 1596, 1597, 1598, 1599, 1600, 1601, 1602, 1603, 1604}
	vsQuestBets := []struct {
		IsTicket bool
		Quantity uint32
	}{
		{true, 5},
		{false, 1000},
		{false, 5000},
		{false, 10000},
	}

	data, _ := hex.DecodeString("")
	bf.WriteBytes(data)

	bf.WriteUint16(uint16(len(vsQuestItems)))
	bf.WriteUint32(uint32(len(vsQuestBets)))
	bf.WriteUint16(0) // Unk

	for i := range vsQuestItems {
		bf.WriteUint16(vsQuestItems[i])
	}
	for i := range vsQuestBets {
		bf.WriteBool(vsQuestBets[i].IsTicket)
		bf.WriteUint8(9)
		bf.WriteUint16(7)
		bf.WriteUint32(vsQuestBets[i].Quantity)
	}

	bf.WriteUint16(totalCount)
	bf.WriteUint16(skip)
	bf.Seek(0, io.SeekStart)
	bf.WriteUint16(returnedCount)
	quests = bf.Data()
	return
}
